    function isNumberKey(evt) {
        var charCode = (evt.which) ? evt.which : evt.keyCode;
        if (charCode != 46 && charCode > 31 &&
            (charCode < 48 || charCode > 57))
            return false;

        return true;
    }

    function chkcharacter(evt) {
        var charCode = (evt.which) ? evt.which : evt.keyCode;
        if (charCode == 34) {
            alert("Double quotes are not allowed.");
            return false;
        } else {
            return true;
        }
    }

