function img_rotate(string $filePath_boxpic_org): void {
	// Get the EXIF data
	$exif = exif_read_data($filePath_boxpic_org);

	// Check if the EXIF data contains orientation information
	if (!empty($exif['Orientation'])) {
		//echo "Orientation - " . $exif['Orientation'] . "<br>";
		switch ($exif['Orientation']) {
			case 3:
				// Rotate the image 180 degrees
				$filePath_boxpic_orgImg = imagecreatefromjpeg($filePath_boxpic_org);
				$filePath_boxpic_org1 = imagerotate($filePath_boxpic_orgImg, 180, 0);
				imagejpeg($filePath_boxpic_org1, $filePath_boxpic_org);
				//echo "Done Rotate 3 <br>";
				break;
			case 6:
				// Rotate the image 90 degrees clockwise
				$filePath_boxpic_orgImg = imagecreatefromjpeg($filePath_boxpic_org);
				$filePath_boxpic_org1 = imagerotate($filePath_boxpic_orgImg, -90, 0);
				
				imagejpeg($filePath_boxpic_org1, $filePath_boxpic_org);
				//echo "Done Rotate 6 <br>";
				break;
			case 8:
				// Rotate the image 90 degrees counter-clockwise
				$filePath_boxpic_orgImg = imagecreatefromjpeg($filePath_boxpic_org);
				$filePath_boxpic_org1 = imagerotate($filePath_boxpic_orgImg, 90, 0);
				imagejpeg($filePath_boxpic_org1, $filePath_boxpic_org);
				//echo "Done Rotate 8 <br>";
				break;
			// Add additional cases for other orientation values if needed
		}
	}
}
										
