function watermark_image(string $target, string $wtrmrk_file, string $newcopy): void {
    $watermark = imagecreatefrompng($wtrmrk_file);
    imagealphablending($watermark, false);
    imagesavealpha($watermark, true);
    $img = imagecreatefromjpeg($target);
    $img_w = imagesx($img);
    $img_h = imagesy($img);
    $wtrmrk_w = imagesx($watermark);
    $wtrmrk_h = imagesy($watermark);
    $dst_x = ($img_w / 2) - ($wtrmrk_w / 2); // For centering the watermark on any image
    $dst_y = ($img_h / 2) - ($wtrmrk_h / 2); // For centering the watermark on any image
    imagecopy($img, $watermark, $dst_x, $dst_y, 0, 0, $wtrmrk_w, $wtrmrk_h);
    imagejpeg($img, $newcopy, 100);
    imagedestroy($img);
    imagedestroy($watermark);
}

function work_as_kit_box_calculate(
    int $bid, 
    string $type, 
    float $blength, 
    ?string $blength_frac, 
    float $bwidth, 
    ?string $bwidth_frac, 
    float $bdepth, 
    ?string $bdepth_frac
): string {
	//check box type Medium, Kit - Large, Kit - X-Large, Shipping Box (UCB), and Shipping Box (Non-UCB)
	if($type=="Medium" || $type=="Large" || $type=="Xlarge" || $type=="Box" || $type=="Boxnonucb" || $type=="Loop" || $type=="Presold")
	{    
		
	//box length fraction
		if($blength_frac!="")
		{
			$frac=explode("/",$blength_frac);
			$numerator=$frac[0];
			$denominator=$frac[1];
		   
			//echo $blength_frac."<br>";
			//$box_length=$blength+$numerator / $denominator;
			$box_length = $blength + (float)$numerator / (float)$denominator;

		}
		else{
			$frac="";
			$box_length=$blength;
		}
		//box width fraction
		if($bwidth_frac!="")
		{
			$frac=explode("/",$bwidth_frac);
			$numerator=$frac[0];
			$denominator=$frac[1];
			$box_width = $bwidth + (float)$numerator / (float)$denominator;
		}
		else{
			$frac="";
			$box_width=$bwidth;
		}
	   
	   //box height fraction
		if($bdepth_frac!="")
		{
			$frac=explode("/",$bdepth_frac);
			$numerator=$frac[0];
			$denominator=$frac[1];
			$box_height = $bdepth + (float)$numerator / (float)$denominator;
		}
		else{
			$frac="";
			$box_height=$bdepth;
		}
		
		//Calculate cubic feet 
		$cal_cubic_feet =($box_length*$box_width*$box_height) / 1728;
		$cubic_feet=number_format($cal_cubic_feet,2);
		//echo $cubic_feet."<br>";
		//
		//find Medium? using formula =IF(AND(B5>=10,C5>=10,D5>=10,B5+C5<39,C5+D5<27.5),"Yes","No")
			$box_lw=$box_length+$box_width;
			$box_wh=$box_width+$box_height;
			 if($box_length>=10 && $box_width>=10 && $box_height>=10 && $box_lw<39 && $box_wh<27.5)
			 {
				 $medium="Yes";
			 }
			else{
				 $medium="No";
			}

		//Large 1 Side Flaps? using formula =IF(AND(B5>=10,C5>=10,D5>=10,B5+C5<39,C5/2+D5<27.5),"Yes","No")

			$box_wh1=($box_width/2)+$box_height;
			if($box_length>=10 && $box_width>=10 && $box_height>=10 && $box_lw<39 && $box_wh1<27.5)
			 {
				 $Large1SideFlaps="Yes";
			 }
			else{
				 $Large1SideFlaps="No";
			}
		//Large Fold Seam? using formula =IF(AND(B5>=10,C5>=10,D5>=10,B5<27.5,C5+D5<39),"Yes","No") 
			if($box_length>=10 && $box_width>=10 && $box_height>=10 && $box_length<27.5 && $box_wh<39)
			 {
